import{_ as e,g as a}from"./app.629fec38.js";const r={},t=a('<p>\u968F\u7740\u8D8A\u6765\u8D8A\u591A\u7684\u64CD\u4F5C\u7CFB\u7EDF\u3001\u6D4F\u89C8\u5668\u5F00\u59CB\u652F\u6301 Dark Mode \u548C\u76F8\u5E94\u7684 Media Queries Level 5\uFF0C\u8D8A\u6765\u8D8A\u591A\u7684\u7F51\u7AD9\u5F00\u59CB\u6DFB\u52A0\u6DF1\u8272\u6A21\u5F0F\u3002</p><h2 id="\u5173\u4E8E\u6DF1\u8272\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u6DF1\u8272\u6A21\u5F0F" aria-hidden="true">#</a> \u5173\u4E8E\u6DF1\u8272\u6A21\u5F0F</h2><p>Dark themes reduce the luminance emitted by device screens, while still meeting minimum color contrast ratios. They help improve visual ergonomics by reducing eye strain, adjusting brightness to current lighting conditions, and facilitating screen use in dark environments \u2013 all while conserving battery power.</p><blockquote><p>\u7FFB\u8BD1 \u6DF1\u8272\u4E3B\u9898\u80FD\u964D\u4F4E\u8BBE\u5907\u5C4F\u5E55\u53D1\u51FA\u7684\u4EAE\u5EA6\uFF0C\u540C\u65F6\u4ECD\u7136\u6EE1\u8DB3\u6700\u5C0F\u7684\u989C\u8272\u5BF9\u6BD4\u5EA6\u3002\u5B83\u4EEC\u6709\u52A9\u4E8E\u6539\u5584\u89C6\u89C9\u6548\u679C\u3001\u51CF\u5C11\u773C\u775B\u75B2\u52B3\uFF0C\u8C03\u6574\u4EAE\u5EA6\u4EE5\u9002\u5E94\u5F53\u524D\u7684\u7167\u660E\u6761\u4EF6\uFF0C\u5E76\u6709\u52A9\u4E8E\u5728\u9ED1\u6697\u73AF\u5883\u4E2D\u4F7F\u7528\u5C4F\u5E55\uFF0C\u540C\u65F6\u8282\u7701\u7535\u6C60\u7535\u91CF\u3002</p></blockquote><h2 id="\u754C\u9762\u8BBE\u8BA1" tabindex="-1"><a class="header-anchor" href="#\u754C\u9762\u8BBE\u8BA1" aria-hidden="true">#</a> \u754C\u9762\u8BBE\u8BA1</h2><p>Google \u5728 Material Design \u7684\u8BBE\u8BA1\u6307\u5357\u4E2D\u5BF9\u4E8E\u6DF1\u8272\u6A21\u5F0F\u5217\u51FA\u4E86\u4E00\u7CFB\u5217\u8BBE\u8BA1\u89C4\u8303\uFF0C\u5176\u4E2D\u7B2C\u4E00\u6761\u5C31\u662F \u300C\u4E0D\u8981\u4F7F\u7528 100% \u7684\u7EAF\u9ED1\u300D \u3002</p><p>Google \u63A8\u8350\u5C06\u6DF1\u8272\u8868\u9762\u548C 100% \u767D\u8272\u6587\u5B57\u7684\u5BF9\u6BD4\u5EA6\u81F3\u5C11\u8BBE\u7F6E\u4E3A\u4E3A 15.8:1 \uFF08Dark surfaces and 100% white body text have a contrast level of at least 15.8:1\uFF09</p><p>\u4E3A\u4EC0\u4E48\u5462\uFF1F\u56E0\u4E3A\u7EAF\u767D\u8272\u4F1A\u53CD\u5C04\u6240\u6709\u6CE2\u957F\u7684\u5149\u7EBF\uFF0C\u800C\u7EAF\u9ED1\u8272\u4F1A\u5438\u6536\u6240\u6709\u5149\u7EBF\uFF0C\u8FD9\u662F\u5BF9\u6BD4\u5EA6\u6700\u5927\u7684\u4E24\u79CD\u989C\u8272\uFF0C\u767D\u5E95\u9ED1\u5B57\u65F6\uFF0C\u6587\u5B57\u8FC7\u4E8E\u523A\u773C\uFF0C\u800C\u9ED1\u5E95\u767D\u5B57\u65F6\uFF0C\u6587\u5B57\u53C8\u53EF\u80FD\u96BE\u4EE5\u8FA8\u8BA4\u3002\u5982\u679C\u957F\u65F6\u95F4\u9605\u8BFB\u8FD9\u6837\u7684\u6587\u5B57\uFF0C\u52BF\u5FC5\u4F1A\u8BA9\u773C\u775B\u611F\u89C9\u5230\u75B2\u52B3\uFF0C\u4E0E\u6DF1\u8272\u6A21\u5F0F\u5F00\u53D1\u7684\u521D\u8877\u80CC\u9053\u800C\u9A70\u3002</p><p>Google \u63A8\u8350\u4F7F\u7528 <code>#121212</code> \u6765\u4F5C\u4E3A\u6DF1\u8272\u6A21\u5F0F\u7684\u4E3B\u80CC\u666F\u8272\u3002</p><p>A dark theme uses dark grey, rather than black, as the primary surface color for components. Dark grey surfaces can express a wider range of color, elevation, and depth, because it\u2019s easier to see shadows on grey (instead of black). Dark grey surfaces also reduce eye strain, as light text on a dark grey surface has less contrast than light text on a black surface.</p><blockquote><p>\u7FFB\u8BD1 \u6DF1\u8272\u4E3B\u9898\u5E94\u4F7F\u7528\u6DF1\u7070\u800C\u4E0D\u662F\u9ED1\u8272\u4F5C\u4E3A\u7EC4\u4EF6\u7684\u4E3B\u8981\u8868\u9762\u989C\u8272\u3002\u6DF1\u7070\u8272\u8868\u9762\u53EF\u4EE5\u8868\u793A\u66F4\u5E7F\u6CDB\u7684\u989C\u8272\uFF0C\u9AD8\u5EA6\u548C\u6DF1\u5EA6\u8303\u56F4\uFF0C\u56E0\u4E3A\u66F4\u5BB9\u6613\u770B\u5230\u7070\u8272\u9634\u5F71\uFF08\u800C\u4E0D\u662F\u9ED1\u8272\uFF09\u3002 \u6DF1\u7070\u8272\u7684\u8868\u9762\u8FD8\u53EF\u4EE5\u51CF\u5C11\u773C\u775B\u75B2\u52B3\uFF0C\u56E0\u4E3A\u6DF1\u7070\u8272\u8868\u9762\u4E0A\u7684\u6D45\u8272\u6587\u672C\u7684\u5BF9\u6BD4\u5EA6\u4F4E\u4E8E\u9ED1\u8272\u8868\u9762\u4E0A\u7684\u6D45\u8272\u6587\u672C\u3002</p></blockquote>',11);function s(o,n){return t}var c=e(r,[["render",s]]);export{c as default};
